float boxDist(vec2 p, vec2 size, float radius)
{
	size -= vec2(radius);
	vec2 d = abs(p) - size;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}

float intersect(float d1, float d2)
{
	return max(d1, -d2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 cord = uv;
    vec3 l = vec3(.9);
    float r = .5+.5*pow(.5+.5*cos(atan(cord.x, cord.y)*8. + iTime), .1);
    l *= 1. - smoothstep(r, r+.001, length(cord)*3.);

     l = mix( l, vec3(.8, .2, .5), step(length(cord), .15 ));
    //cord.x -= .07;
    cord.y -= .05;
    r = .1;

     float eyeR = smoothstep(r, r+.001, intersect(boxDist(vec2(cord.x, cord.y - .197), vec2(r), .0),
     5.*(r*.1 - abs(r*.9 - length(vec2(.07 - abs(cord.x), cord.y))*3.))));

    l *= eyeR;

    cord.y += .036;
    r = .4;
    float smile = smoothstep(r, r+.001, intersect(boxDist(vec2(cord.x, cord.y + .55), vec2(.1), .0),
     115.*(r*.1 - abs(r*.9 - length(vec2(cord.x*1.3, cord.y))*3.))));

    l *= smile;


    l = mix(mix(vec3(.1, .4, .1), vec3(.3, .9, .2), uv.y ), l, l.r);


    fragColor = vec4(l, 1.);

}
