# define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float size = 0.2;
    float speed = 1.5;

    uv.y += sin(uv.x*PI*2.0+iTime * speed) * 0.07;
    float rect = step(size, uv.x) - step(1.0 - size, uv.x);
    rect *= step(size, uv.y) - step(1.0 - size, uv.y);

    vec3 col = vec3(0.0);
    col += rect * vec3(cos(uv.x), sin(uv.y+iTime * speed), tan(uv.x + uv.y + iTime * speed));

    // Output to screen
    fragColor = vec4(col,1.0);
}
