float PI = 3.14159;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    float l = length(uv) * 2. * PI;
    float th = atan(uv.y, uv.x);
    float cycles = 5.;
    float ft = 4. * sin(iTime * 0.7);
    float p = 0.1 + 3. * 0.5 * (1. + sin(iTime * 1.));
    float _int = 1. + 10. * 0.5 * (1. + sin(iTime * 0.2));
    float fth = pow(0.5 * (1. + sin(th * cycles + iTime + sin(l * 3.) * ft)), p);
    fth = fth * pow(0.5 * (1. + sin(length(uv) * 2. * PI * _int + iTime * 3.)), 1.);
    vec3 col = vec3(fth * 1.0, fth * 0.9, fth * 0.8);

    fragColor = vec4(col,1.0);
}
