#define s(x) smoothstep(0.15, 0.3, x * 1.1 - 0.1)

vec3 chromaKey(vec3 x, vec3 y){
	vec2 c = s(vec2(x.g - x.r * x.y, x.g));

    return mix(x, y, c.x * c.y);
}

vec3 getTexture(vec2 p){
	vec4 s = texture(iChannel0, p);
    return s.xyz * s.w;
}

vec3 getTexture1(vec2 p){
	vec4 s = texture(iChannel1, p);
    return s.xyz * s.w;
}
const mat2 rot = mat2( 0.6,  0.2, -0.2,  0.6 );

vec2 hash( float n )
{
    float sn = sin(n);
    return fract(vec2(sn,sn*42125.13));
}

float circleNoise( vec2 uv )
{
    float uv_y = floor(uv.y);
    uv.x += uv_y*.31;
    vec2 f = fract(uv);
    vec2 h = hash(floor(uv.x)*uv_y);
    float m = (length(f-.25-(h.x*.5)));
    float r = h.y*.25;
    return m = smoothstep(r-.10*r,r,h.x);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= sin(iTime*.5)+2.0;
    uv += iTime*.4;
    vec2 orig=uv;
    vec2 offset=vec2(cos(iTime)*.5,sin(iTime)*.25);
    uv=orig+=offset;


    float m = 1.0;
    for(float i=1.;i<=3.;i++)
    {
    	uv = uv*rot*2.0+1121.13;
    	m *= clamp(circleNoise(uv)+.25*i,0.,1.);  
    }    
    fragColor = vec4(m);
    if(fragColor.x>=.125)fragColor=texture(iChannel0,orig);
	if(fragColor.x>=.075 && fragColor.x<=.09375)fragColor=mix(fragColor,texture(iChannel3,orig),offset.x);
	if(fragColor.x>=.5)fragColor=texture(iChannel2,orig);
	//if(fragColor.x<=.06)fragColor=texture(iChannel1,orig);



    vec3 background = getTexture1(uv);


    uv=(fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    float mask = smoothstep(0.,.1,1.-length(uv)-.5);

    vec3 color = getTexture(uv);
         color = chromaKey(color, background);

         vec2 LEFTTOP =vec2(.5,.5);
         vec4 lt;
         vec4 lb;
         vec4 rt;
         vec4 rb;

         //LT
        if(orig.x>=LEFTTOP.x || orig.y<=LEFTTOP.y){lt=texture(iChannel0,orig/LEFTTOP);}
        //LB 
        if(orig.x>=LEFTTOP.x || orig.y>=LEFTTOP.y){lb=texture(iChannel1,orig/LEFTTOP);}
        //RT
        if(orig.x<=LEFTTOP.x || orig.y<=LEFTTOP.y){rt=texture(iChannel2,orig/LEFTTOP);}
        //RB
        if(orig.x<=LEFTTOP.x || orig.y>=LEFTTOP.y){rb=texture(iChannel3,orig/LEFTTOP);}



        if( lt.x==0. )color=chromaKey(texture(iChannel0,orig/LEFTTOP).rgb, lb.rgb);
        if( rt.x==0. )color=chromaKey(texture(iChannel0,orig/LEFTTOP).rgb, rb.rgb);
        if( lb.x==0.)color=chromaKey(texture(iChannel2,orig/LEFTTOP).rgb, rb.rgb);
        if( rb.x==0.)color=chromaKey(texture(iChannel2,orig*.5+.0125).rgb, lb.rgb);

	//fragColor = vec4(color,1.0);
}
