// reference: https://en.wikipedia.org/wiki/Thermochronology
// variant of https://shadertoy.com/view/NdVyzW

void mainImage( out vec4 O, vec2 u )
{
    vec2 U = u/R;
    O = T(u);                     // red = hot, blue = cold
    O.b = 1.-O.r;
                                  // temperature curve with height
    float v = .5 + (O.r-.5) / exp(4.*(U.y-1.)) - U.x; // amplifying the signal with depth
    O = mix(O, vec4(0,1,0,1), smoothstep(1.5,0., abs(v)/fwidth(v)) ); 

    if ( int(u)==int(R)/2 ) O++;  // ref axis
}

#define S(n) ( .5+.5*sin(n*iTime) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 U = u/R;
    O = textureLod(iChannel0, U,floor(log2(R.y)-6.) ); // diffusion
        if ( U.y>.95 ) O.r = (  S(10.)                 // daily temperature variations
                          + S( 1.)                     // "seasonal" temperature variations
                          + S( .1) ) /3.;              // "climatologic" temperature variations
    if (iFrame<1) O.r=.5;                              // initialization
}
