void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =50.0 * fragCoord/iResolution.xy-25.0;
    float x = uv.x - sin(iMouse.x / 100.0);
    float y = uv.y * (iResolution.y / iResolution.x);
    float a = sin(iTime);
    vec3 col;
    float gx = (abs(y) <= 0.25)? abs(0.025 / y):0.0; 
    float gy =(abs(x) <= 0.25)? abs(0.025 / x):0.0;
    float g = gx + gy;

    x -= 45.0 * sin(iTime / 10.0);
     x /= 4.0;
     y/= -4.0;

     float c = y - a / 1000.0 - sin(3.0 * y / (x + a * y));
     c = 0.1 / c;
    if (c >= 0.0) {
                    col = vec3(c  ,g ,0.0);
                  }
     else {
            col = vec3( 0.0,g, -c);
          }

    fragColor = vec4(col,1.0);
}
