void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float chromAmount = iMouse.y / iResolution.y / 50.;

    float redChan = texture(iChannel0, uv + vec2(chromAmount, 0.)).r;
    float greenChan = texture(iChannel0, uv).g;
    float blueChan = texture(iChannel0, uv - vec2(chromAmount, 0.)).b;

    vec4 o = vec4(redChan, greenChan, blueChan, 1.);

    fragColor = o;
}
