void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.x*= 3.14159;
    uv.x+=iTime*.5;

    float r = abs(sin(uv.x));
    float g =abs(sin(uv.x+2.));
    float b = abs(sin(uv.x+4.));

    vec3 col = vec3(r,g,b);
    fragColor = vec4(col,1.0);
}
