void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 u = uv*2.-1.;
    float t= abs(sin(iTime * 0.5)-1.0);

    float a = 1.-smoothstep(abs(u.x), 0.3, 0.28);
    float b = 1.-smoothstep(abs(u.y), 0.8, 0.78);         
    float c = smoothstep(uv.y, uv.y, t);

    vec4 color = vec4(0.1, 0.9, 0.2, 1.0);
    if (t <= 0.1) {
        color = vec4(0.9, 0.1, 0.2, 1.0);
    } else if (t <= 0.3) {
        color = vec4(0.9, 0.9, 0.1, 1.0);
    }
    vec4 outColor = color*c;
    outColor += color*(a+b);

    // Output to screen
    fragColor = outColor;

    //discard
    if (abs(u.x) > 0.3 || abs(u.y) > 0.8) {
        fragColor = vec4(0.0);
    }    
}
