void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float speed = 0.07;

    vec3 bgCol = vec3(0.0, 0.0, 0.0);
    vec3 fgCol = vec3(1.0, 1.0, 1.0);

    int numSegments = 20;

    float segmentWidth = 1.0 / float(numSegments);

    vec3 col = bgCol;

    for(int i = 0; i < numSegments * 2; i++) {
        if (uv.x + mod(speed * iTime, 1.0) < float(i + 1) * segmentWidth) {
            if (i % 2 == 0) {
                col = fgCol;
            }
            break;
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0); //RGBA
}
