//change this variable to change the pattern
#define pattern vec4(0,0.0,0.8,.5)

#define ITERS 12
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    float t=0.0;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/20.0;
    uv.y += (iTime)/25.0;
    for(int c=0;c<ITERS;c++){
        float scale = 2.16;
        float scale1 = 1.16;
        col_prev = col;

        for(int i=0;i<ITERS;i++)
        {
            float uv1 = uv.x-uv.y;
            uv= fract(pattern.z-uv-(((vec2(uv1+pattern.x*col.x,-pattern.y-uv1))/(scale1*scale*scale))))/scale1;
            uv.x *= scale1;
            uv = fract(pattern.z+uv.yx);
            uv.y /= -scale1;
            uv -= fract(pattern.w+(uv.x-uv.y)/4.0);
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev.yzx))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}
