void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float ygrid = step(sin(uv.y*30. + 5.*iTime)+1., 1.);
    float xgrid = step(sin(uv.x*30. + 5.*iTime)+1., 1.);

    float bgrid = 1.0 - ygrid - xgrid;

    vec3 col = vec3(xgrid,ygrid,bgrid);
    // Output to screen
    fragColor = vec4(col,1.0);
}
