#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
void mainImage( out vec4 rgba, in vec2 o){
    rgba=vec4(0.0);
    o-=iResolution.xy/2.0;
    o/=iResolution.y/2.5;
    vec2 o2=o*rot(radians(60.0));
    vec2 o3=-o*rot(radians(-60.0));
    float Time=iTime*4.0;
    vec3 A=vec3((o2.x),o3.x,(o.x+(cos(Time)/2.5)));
    rgba+=vec4(length(vec3(A.x,A.y,A.z-(cos(Time)/2.5)))*abs(o.y+1.0)<2.0)*float(length(A)>=1.0-abs(cos(Time))*0.1);
    rgba*=vec4(length(vec3(A.x,A.y,A.z-(cos(Time)/2.5)))*abs(o.y-1.0)<2.0)*float(length(A)>=1.0-abs(cos(Time))*0.1);
    rgba*=vec4(1,length(vec3(A.x,A.y,(A.z)-(cos(Time)/2.5)))<1.67,length(vec3(A.x,A.y,A.z-(cos(Time)/2.5)))<1.67,1)+0.67;
    rgba+=vec4(-A.x+(sin(0.0)),(A.y-(sin(0.0))),(((A.z)-(cos(Time)/5.0))-(sin(0.0))),1.0)*float(length(A)<1.0-abs(cos(Time))*0.1);
    rgba+=vec4((length(vec3(A.x,A.y,A.z))+1.0)/4.0*float(length(A)<1.0-abs(cos(Time))*0.1));
    rgba*=float(length(vec3(A.x,A.y,A.z))>clamp(abs(1.0+cos(iTime/1.0))*0.5,0.5,0.67));
}
