float k = .9;
float w = 4.;
float CLA = 1.;
float amp = .4;

float speaker(vec2 uv)
{
    float sp = 0.;
    sp = smoothstep(2.05, 1.95, length(uv) + amp/CLA*clamp(sin(k*2.-w*iTime), -CLA, CLA));

    return sp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    uv.y -= .5;
    uv.x -= .5 * iResolution.x/iResolution.y;

    uv *= 30.;
    vec2 id = floor(uv) + vec2(0.5);

    float dx = amp/CLA * clamp(sin(k * length(id) - w * iTime), -CLA, CLA);
    float sp = speaker(uv);

    vec2 loc_uv = fract(uv) - 0.5;
    loc_uv += dx * normalize(id);

    float air = smoothstep(0.12, 0.1, length(loc_uv));
    vec3 col = vec3(air);

    //col.r += .2 * cos(k * id.x - w * iTime);
    //col.g -= .2 * cos(k * id.x - w * iTime);

    col += vec3(sp);

    fragColor = vec4(col, 1.);
}
