//Arrangemonk 2021, innefficient noise function
//in the unlikely event of someone forking this: yeah, do that

float PI  = 3.14159265359;
float TAU = 6.28318530718;
float E = 2.71828182846;
float iters = 1.;
float octaves = 32.;

float GammaToLinear(float gamma)
{
    return pow(gamma,0.4545454545454545);
}

vec3 GammaToLinear(vec3 gamma)
{
    return vec3(GammaToLinear(gamma.x),GammaToLinear(gamma.y),GammaToLinear(gamma.z));
}

float LinearToGamma(float linear)
{
    return pow(linear,2.2);
}

vec3 LinearToGamma(vec3 linear)
{
    return vec3(LinearToGamma(linear.x),LinearToGamma(linear.y),LinearToGamma(linear.z));
}


float flip(float x)
{
    return sign(cos(x * PI));
}

vec2 rot(vec2 uv, float phi)
{
    return vec2(sin(phi)*uv.x + cos(phi)*uv.y,cos(phi)*uv.x - sin(phi)*uv.y);
}


vec3 rotfbm(vec3 pos)
{
vec3 result = vec3(0.);
float m = 0.;
for(float i = 1.; i <= octaves;i++)
    {
    float f =flip(i);
    float tmp = 1./i;
    m += (f * tmp);
    result += f * GammaToLinear(texture(iChannel0,rot(pos.xy*i * iters,PI * E * i)).xyz)* tmp;
    }
return mix(result /m,result *m,.5);
}


vec3 rotfbmp2(vec3 pos)
{
vec3 result = vec3(0.);
float m = 0.;
for(float x = 1.; x <= octaves;x++)
    {
    float i = pow(2.,x);
    float f =flip(i);
    float tmp = 1./i;
    m += (f * tmp);
    result += f * GammaToLinear(texture(iChannel0,rot(pos.xy*i * iters,PI * E * x)).xyz)* tmp;
    }
return mix(result /m,result *m,.5);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    float scale = sin(iTime* 0.05 * TAU)*0.5;
    uv = (uv-vec2(.5,.25)) * mix(.001,1.,1.+scale) + vec2(.5,.0);

    vec3 val = vec3(0.);
    if(uv.x < .5)
    val = rotfbmp2(vec3(uv,iTime));
    else
    val = rotfbm(vec3(uv,iTime));


    uv = fragCoord/iResolution.x;
    if (uv.x > .499 && uv.x < .501 ) {
        val = vec3(0.0);
    }
    val = LinearToGamma(val);

    fragColor = vec4( val,1.0);
}
