void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    float cambioAng = 0.;

    for(int i=0;i<15;i++)
    {
        float ang = 0.7 + cambioAng;
        mat2 M = mat2(cos(ang), -sin(ang), sin (ang), cos(ang));
        vec2 uvTentaculo = (2.0 * fragCoord - iResolution.xy) / iResolution.xy;
        uvTentaculo *=M;
        uvTentaculo.x += iTime;
        float h = smoothstep(0.,uvTentaculo.y + 0.1 * sin (4.0 * uvTentaculo.x), .05 );
        vec3 colorTentaculo = vec3 (h);
        colorTentaculo *= vec3(0.0,0.0,1.0);
        col+=colorTentaculo;
        cambioAng+=0.2;
    }

