void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 c = fragCoord - 0.5*iResolution.xy; // [-Res.xy/2 ~ Res.xy/2] definition of center coordinates 
    // c = 2.*ceil(c/2.); // [-Res.xy/2 ~ Res.xy/2] pixelates
    float pi = 3.141592;
    float ang = atan(c.y,c.x); // [-pi ~ pi] angular coordinate
    float dist = sqrt(c.x*c.x+c.y*c.y); // [0 ~ iRes/2] polar distance coordinate
    float spiral = dist*0.1 + 5.*(iTime); // spiral function
    float red = ceil(-0.5+sin(spiral)); // square wave from spiral function
    float grn = ceil(-0.5+sin(spiral+2.*pi/3.));
    float blu = ceil(-0.5+sin(spiral-2.*pi/3.));
fragColor = vec4( vec3(1)-vec3(red , grn , blu) , 1.0); // output: R,G,B,alpha
}
