void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float amount = sin(iTime)*0.03;
    float split = 1.0-fract(iTime / 8.); 
    float scanOffset = 0.1;

    vec2 uv1 = vec2(uv.x + amount, uv.y);

    if(uv.y > split && uv.y < split + scanOffset){
        //x的偏移量是固定的，y的偏移量随时间正弦波动
        uv1.x += scanOffset;
        uv1.y += scanOffset*sin(iTime/2.0);
    }else{
        uv1 = uv;
    }
    vec4 texColor = texture(iChannel0, uv1);

	fragColor = texColor;
    fragColor.a = 1.0;
}
