void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pc = (uv-.5)*vec2(1, iResolution.y/iResolution.x);
    vec2 tc = pc* (2.00/ pow( abs((log(length(pc))))  , cos(iTime*.1)  )  )*0.85;
    fragColor = texture(iChannel0,tc-vec2(.5,.5));
}
