float Hash21(vec2 p){
    p = fract(p*vec2(234.34,435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 screenUV = fragCoord.xy/iResolution.xy;

    uv += iTime*.1;
    uv *= 5.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float n = Hash21(id); //random number between 0-1
    float width =.25;

    if(n<.5) gv.x *= -1.;
    float d = abs(abs(gv.x+gv.y)-.5);
    vec2 cUv = gv-sign(gv.x+gv.y+.001)*.5;
    d = length(cUv);
    float mask = smoothstep(.01,-.01, abs(d-.5)-width);
    float angle = atan(cUv.x,cUv.y); // -pi to pi
    float checker = mod(id.x+id.y,2.)*2.-1.;
    float flow = sin(iTime+checker*angle*2.);

    float x = checker*angle/1.57-iTime*.3;
    float y = (d-(.5-width))/(2.*width);
    y = abs(y-.5)*2.;
    vec2 tUv = vec2(x/2.,y);

    vec4 col = texture(iChannel0,fract(tUv))*mask;
    if(col.a < .75) col = vec4(0.05,0,0.025,1);

    col.a = 1.;
    //if(gv.x>.45 || gv.y>.45) col = vec3(1,0,0);
    fragColor = col;
}
