#define PI 3.14159265359

vec2 u(in float x, in float t) {
    float y = 0.0;
    float dx = 0.0;
    float s = 4.0 / PI;
    for (float n=0.0; n < 25.0; n++) {
        float k = 2.0 * n + 1.0, kx = k * x;
        float w = exp(-k * k * t);
        y += s / k * cos(kx) * w;
        dx -= s * sin(kx) * w;
        s = -s;
    }

    return vec2(y, dx);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 8.0;
    vec2 y = u(uv.x, max(0.0, iTime < 10.0 ? 0.5 * (iTime - 2.0) : iMouse.x / iResolution.x * 5.0));
    float stroke = smoothstep(10.0 / iResolution.y, 0.0, abs(uv.y - y.x) / sqrt(1.0 + y.y * y.y));
    fragColor = vec4(stroke, 0.0, stroke, 1.0);
    fragColor.rb *= mix(vec2(0.0, 1.0), vec2(1.0, 0.0), 0.5 + 0.5 * y.x);
}
