void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float frequency = 1.0;
    //float index = uv.x + (iTime * 0.5) + uv.y;
    float index = (uv.x * 0.5) + uv.y - (iTime * 0.3) ;
    fragColor = sin(frequency * index + vec4(0,2,4,0)) * 0.2 + 0.8;
}
