void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pos = fragCoord.xy; //coordenadas de la superficie
    vec4 solidColor = vec4(0, 0, 0, 1);// color solido negro
    if(pos.x > 400.0)//si la coordenada en x es mayor a las 400 unidades (n√∫mero al azar), entonces volverlo 1
    //en el canal r (color rojo)
    {
        solidColor.r = 1.0;
    }
    fragColor = solidColor; //enviar el color
}
