#define hash21(p) fract(sin(dot(p, vec2(122.9898, 78.2033))) * 43758.5453)

float fig(vec2 uv,vec2 offset){
vec2 uuv= uv;
   uv+=offset;
 float d = length(uv)-.052;
    d = smoothstep(.010,.011,d);
    float c = d;


    for(float i=0.;i<25.;i++){
        vec2 n = uv;
        float h  = hash21(vec2(i,i+.12)+offset);

        float at= atan(n.x,n.y);

       float q=length(uv)-(.120+.018*i+sin(iTime*.33+at*9.+h*h*h*h*exp(mod(i+7.,14.)))*(.01+.001*i+h*.008));
       //q = smoothstep(.0040,.0071,abs(q)-.001-sin(iTime+i+at*2.+h*100.)*.005+.003);
       q = smoothstep(0., fwidth(q),abs(q)-.001-sin(iTime+i+at*2.+h*100.)*.005-.0001);
       d = min(q,d);
    }

    float lim = .47;
    if(abs(uuv.x) >lim || abs(uuv.y) > lim){
        d=1.;
    }
    d = min(c,d);
    return d;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;


     vec2 offset = vec2(-.4,.4);
    float d = fig(uv,offset);
    float e = fig(uv,offset*-1.);
    d  =min(d,e);
    vec3 col = vec3(d);
    col = mix(vec3(.1),vec3(1.,.95,.9),col);
    fragColor = vec4(col,1.0);
}
