#define S(U,w) min( p = smoothstep(-1., 1., ( abs(U) - w *vec2(.5,R.y/R) )/fwidth(U) ), p.y ).x

vec3 Flag(vec2 U) {

    vec2 R = iResolution.xy, V, p,t;
    U -= .5;
    V = ( U + vec2(U.y,-U) ) / 1.4;

	vec4 O;
    O = mix( O = vec4(.78, .1, .2, 0), 
             1. + S(U,.166 ) * mix( O-1.,                            // s = .75;
                                    -vec4(1, .9, .6, 0) * S(V,.125), //.166*s
                                    S( V - .015 * sign(U), .038) )   //.05*s
             , S(U,.1) ) 
      * ( 1. + (cos(t)*.3-.3)*( U+.5) ).x;

	 return O.rgb;
}

float vignette(vec2 U)
{
    return 16. * U.x * (1. - U.x) * U.y * (1. - U.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = uv.x*7.-6.*iTime+uv.y*6.;
    uv.y += sin(t)*.0125;

    vec3 col = Flag(uv);

    col += sin(t)*0.25; // highlights
    col *= .7+cos(t)*.3; // shadows

    col *= 1.0 - .6 * exp2(-1.75 * vignette(uv)); // vignette

    fragColor = vec4(col,1.0);
}
