int calcSnake(ivec2 pos) {
    int x = pos.x; int y = pos.y;
    int res0 = (x * (x - 1) * 4 + 3 * x) + ((x < 0) ? -2*x  +y : -y);
    int res1 = (y * (y - 1) * 4 + 1 * y) + ((y < 0) ?  2*y  -x : +x);
    return max(res0, res1);
}

float calcCol(vec2 uv) {
    float f = float(calcSnake(ivec2(uv)));   
    return fract(f / 240. - iTime / 6.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * vec2(iResolution.x/iResolution.y, 1.) - 0.5;

    uv = floor(uv * 40.);   

    float c = calcCol(uv);
    // Output to screen
    fragColor = vec4(pow(c, 10.));

}
