mat2 M(float th) {
    return mat2(cos(th), sin(th), -sin(th), cos(th));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float th = 0.5 * (1. + thc(4., iTime)) * h21(floor(10. * uv)) - 0.1 * iTime;
    uv = abs(M(th) * uv );

    float m = 0.18;
    float n = 6.;
    for (float i = 0.; i < n; i++) {
        th += 0.3 * (1.-i/n) * thc(2., 4. * th - iTime);
        uv = abs(M(th) * (uv - m));
        m *= 0.5;
    }

    float d = mlength(uv);
    float s = step(d, 1.4 * m);

    fragColor = vec4(s);
}

#define pi 3.14159

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec2 thc(float a, vec2 b) {
    return tanh(a * cos(b)) / tanh(a);
}

vec2 ths(float a, vec2 b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}
