    float Circle(vec2 uv, vec2 p, float r, float blur)
    {
                 float   d = length(uv-p);
                  float   c = smoothstep(r, r-blur, d);
                   return c;
    }

    float Band(float t, float start, float end, float blur)
    {
              float step1 = smoothstep(start - blur, start + blur, t);
              float step2 = smoothstep(end + blur, end - blur, t);

               return step1*step2;
    }

    float Rect(vec2 uv, float left, float right, float bottom, float top, float blur)
    {
              float band1 = Band(uv.x, left, right, blur);
              float band2 = Band(uv.y, bottom, top, blur); 

               return band1*band2;        

    }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

                  vec2 uv = 1.0-2.0*fragCoord/iResolution.xy;
                   uv.x *= iResolution.x/iResolution.y;

                  vec3 col = vec3(0.0);

                   float x = uv.x;
                   float y = uv.y;
                  float circleyel = 0.0;
                  float circlered = 0.0;
                  float circlepur = 0.0;
                  float circleblue = 0.0;
                  float rect = 0.0;
                  float rectred = 0.0;

                   circleyel = Circle(uv, vec2(0.5, 0.2), 0.45, 0.01);
                   circleyel += Circle(uv, vec2(0.55, 0.2), 0.45, 0.45);
                   circleyel -= Circle(uv, vec2(0.5, -1.1), 1.0, 0.01);
                   circleyel -= Circle(uv, vec2(0.35, 0.2), 0.15, 0.01);
                   circleyel += Circle(uv, vec2(0.36, 0.16), 0.15, 0.01);
                   circleyel -= Circle(uv, vec2(0.25, 0.6), 0.1, 0.01);
                   circleyel -= Circle(uv, vec2(0.5, 0.45), 0.1, 0.01);
                   circleyel -= Circle(uv, vec2(0.37, 0.39), 0.05, 0.01);
                     col += vec3(1,1,0)*circleyel;

                   circleblue += Circle(uv, vec2(0.37, 0.39), 0.05, 0.01);
                    col += vec3(0,1,1)*circleblue;

                   circlepur += Circle(uv, vec2(0.5, 0.45), 0.1, 0.01);
                    col += vec3(1,0,0.6)*circlepur;                     


                   circlered += Circle(uv, vec2(0.5, -0.78), 0.1, 0.01);
                   circlered += Circle(uv, vec2(0.5, 1.2), 0.45, 0.01);
                    col += vec3(1,0,0)*circlered;


                   rect = Rect(vec2(x,y), 0.36, 0.6, 0.6, 0.8, 0.005);
                   rect += Rect(vec2(x,y), 0.2, 0.4, 0.55, 0.62, 0.005);
                   rect += Rect(vec2(x,y), -1.2, 0.1, 0.1-x*0.08, 0.3+x*0.08, 0.005);
                    col += vec3(1,1,0)*rect;

                   rectred = Rect(vec2(x,y), 0.22, 0.78, -0.9, 0.2, 0.01);
                   rectred += Rect(vec2(x,y), 0.14-y*0.5, 0.86+y*0.5, -0.9, -0.1, 0.01);
                    col += vec3(1,0,0)*rectred;

                     fragColor = vec4(col, 1.0);

}
