float sdfboxing(vec2 uv){
if(uv.y <.4){
return smoothstep(.29,.3,length(atan(uv.x,uv.y)));// triangle
}
else
 return 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;
    vec4 triangle = vec4(sdfboxing(uv));
    vec4 tr1 = vec4(triangle)  + vec4(.0,.3,1.0,1.0);
    fragColor =tr1;
}
