void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;

    vec3 col = mix(vec3(1. , .3, .0 ) , vec3(1.,.8,.3) ,sqrt((uv.y)+0.5));

    float p =sin(atan(uv.x, uv.y)*8.0 - uv.x*16.0)*0.1+0.2;

    col *= smoothstep(p, p + 0.002, length(uv));

    //Multiplication is the same thing as and
    col *= 1.- (1. - smoothstep(0.015 + sin(uv.y*120.)*0.002 , 0.0151 + sin(uv.y*120.)*0.002, abs(uv.x + sin(uv.y* 0.4)))) * (1. - smoothstep(0. ,0., uv.y));


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
