{
    vec2 e = vec2(-1, 1)*.001;   
    float t1 = dscene(p + e.yxx), t2 = dscene(p + e.xxy),
          t3 = dscene(p + e.xyx), t4 = dscene(p + e.yyy);
    return vec4(normalize(e.yxx*t1 + e.xxy*t2 + e.xyx*t3 + e.yyy*t4), 
        .25/e.y*(t1 + t2 + t3 + t4 - 4. * dscene(p)));
