// you can use this in fnf mods
float waveStrength = 0.5; // a num between 0 and 2 (n > 1 = basically invisible bitmap)
void mainImage( out vec4 fragColor, in vec2 fragCoord ) // the thing :/
{
    vec2 uv = fragCoord/iResolution.xy; // like a pixel thing
    float time = mod(iTime * 1.05,140.0) + 14.0; // so we can have instant colors
    vec3 col = texture(iChannel0, uv).rgb + clamp(waveStrength, 0.0, 2.0)*cos(time * ((uv.y + time / 7.0)* uv.x) / vec3(uv.xy, mod(time, 10.0))); // this is a big maths moment
    fragColor = vec4(col, 1.0); // export to screen
}
