vec3 func(vec2 uv, float R) {
    float wide = 0.15;
    float smoothwide = 0.15;
    float x = uv.x;
    float y = uv.y;
    float r = length(vec2(x, y));//[0,sqrt2]
    float s1 = smoothstep(iTime*0.1-wide, iTime*0.1-wide+smoothwide, r);
    float s2 = smoothstep(iTime*0.1, iTime*0.1+smoothwide, r);
    return vec3(s1-s2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy;
  float ratio = iResolution.x / iResolution.y;
  vec3 col = func(vec2((uv.x-0.5)*ratio,uv.y-0.5), 0.5); 
  fragColor = vec4(col,1.0);
}
