void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Canvas
    vec2 uv = fragCoord/iResolution.xy;
    float floatCanVasX = float(iResolution.x);
    float floatCanVasY = float(iResolution.y);

    //Initial background (the rest) 
    fragColor = vec4(0., 0., 0., 1.);

    //Circle
    float radius = -170.;
    float thickness = 10.*pow(sin(mod(float(iTime), 10.)), 2.) + 1.;

    //This pixel
    float floatThisX = float(fragCoord.x);
    float floatThisY = float(fragCoord.y);

    //Different random phases and sin^2 for [0; 1]
    float time1 = pow(sin(float(iTime)), 2.);
    float time2 = pow(sin(float(iTime) + 1.), 2.);
    float time3 = pow(sin(float(iTime) + 2.), 2.);

    for (float i = 0.; i <= max(iResolution.x, iResolution.y); i+=20.){
        if ((pow(floatThisX-floatCanVasX/2., 2.) + pow(floatThisY-floatCanVasY/2., 2.) 
            <= pow(((radius + i) + 200. * sin(float(iTime))) + thickness/2., 2.)) 
        && (pow(floatThisX-floatCanVasX/2., 2.) + pow(floatThisY-floatCanVasY/2., 2.) 
            >= pow(((radius + i) + 200. * sin(float(iTime))) - thickness/2., 2.))){
            fragColor = vec4(time1, time2, time3, 1.0);
        }
    }

}
