    bool Circle(vec2 point, vec2 center, float r){
        float scaleY = iResolution.y / iResolution.x;
          return (point.x - center.x) * (point.x - center.x) + (point.y   - center.y) * (point.y - center.y) * (scaleY * scaleY)  < r*r; 
    }

    float calculate(vec2 a, vec2 b, vec2 point){
          return (point.x - a.x) * (b.y - a.y) - (point.y-a.y) * (b.x - a.x);
   }

          bool isOnOneSide(vec2 a, vec2 b, vec2 c, vec2 point){
               return calculate(a, b, c) * calculate(a, b, point) >= 0.;
          }

               bool Triangle(vec2 a, vec2 b, vec2 c, vec2 point){
                    return  isOnOneSide(a, b, c, point) && isOnOneSide(b, c, a, point) && isOnOneSide(c, a, b, point);
               }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv_  = 1.0-2.0*fragCoord/iResolution.xy;

    float x_ = uv_.x;
    float y_ = uv_.y;
    float x = uv.x;
    float y = uv.y;

    fragColor = vec4(1,1,1,1);

    // head
    if(Circle(vec2(x, y), vec2(0.32, 0.56), 0.1))
      fragColor = vec4(0.9, 0.9, 0.7, 1.0);
    if(Circle(vec2(x, y), vec2(0.33, 0.5), 0.025))
      fragColor = vec4(1, 0.3, 0.3, 1.0);
    if(Circle(vec2(x, y), vec2(0.37, 0.47), 0.015))
      fragColor = vec4(0, 0, 0, 1.0);
    if(Circle(vec2(x, y), vec2(0.366, 0.48), 0.01))
      fragColor = vec4(0.9, 0.9, 0.7, 1.0);
    if(Circle(vec2(x, y), vec2(0.34, 0.6), 0.015))
      fragColor = vec4(0, 0, 0, 1.0);  
    if(Triangle(vec2(0.42, 0.59), vec2(0.42, 0.53), vec2(0.54, 0.56), vec2(x, y)))
      fragColor = vec4(0.9, 0.9, 0.7, 1.0);
    // body 
    if(Triangle(vec2(0.3, 0.4), vec2(0.3, 0.3), vec2(0.34, 0.3), vec2(x, y)))
      fragColor = vec4(0.9, 0.9, 0.7, 1.0);
    if(Triangle(vec2(0.3, 0.4), vec2(0.34, 0.4), vec2(0.34, 0.3), vec2(x, y)))
      fragColor = vec4(0.9, 0.9, 0.7, 1.0);
    if(Circle(vec2(x, y), vec2(0.32, 0.08), 0.15))
      fragColor = vec4(1, 0.3, 0.3, 1.0);
    if(Circle(vec2(x, y), vec2(0.32, 0.06), 0.15))
      fragColor = vec4(1, 0.3, 0.3, 1.0);
    if(Circle(vec2(x, y), vec2(0.32, 0.04), 0.15))
      fragColor = vec4(1, 0.3, 0.3, 1.0);
    if(Circle(vec2(x, y), vec2(0.32, 0.02), 0.15))
      fragColor = vec4(1, 0.3, 0.3, 1.0);
    // hat 
    if(Triangle(vec2(0.22, 0.52), vec2(0.05, 0.6), vec2(0.24, 0.68), vec2(x, y)))
      fragColor = vec4(1, 0.3, 0.3, 1.0);
}
