void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = ((2.0 * fragCoord - iResolution.xy) / iResolution.y) * 2.0;
    vec2 z = vec2(0.0);
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = cMul(z, z) + cMul(cSin(uv), cCos(uv)) + vec2(sin(iTime), cos(iTime));
        if(dot(z, z) > 65536.0) break;
    }
    l = l - log2(log2(dot(z,z))) + 4.0;
    fragColor = vec4(vec3((l > 99.0) ? 0.0 : sin(l / 20.0)) * vec3(0.4, 1.0, 1.3), 1.0); // Samples - 1 check. Makes interior black
}

vec2 cMul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

vec2 cSin(vec2 z) {
    return vec2(sin(z.x) * cosh(z.y), cos(z.x) * sinh(z.y));
}

vec2 cCos(vec2 z) {
    return vec2(cos(z.x) * cosh(z.y), -sin(z.x) * sinh(z.y));
}
