void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float colx = mod(floor(fragCoord.x/30.0),2.0);
    float coly = mod(floor(fragCoord.y/30.0),2.0);

    vec3 col = vec3(0,0,0);
    if (colx == coly) {
        vec2 uv = fragCoord/iResolution.xy;

        col = 0.5 + 0.5*cos(iTime*10.0+uv.xyx+vec3(0,2,4));
    }

    fragColor = vec4(col,1.0);
}
