vec4 circle(float radius, vec2 position, vec2 uv){
    float glow = 0.009;
    float color = smoothstep(radius, radius-glow, length(uv-position));
    return vec4(vec3(color),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord -.5* iResolution.xy) / iResolution.y;
    vec4 circles = circle(0.1, vec2(0.0, 0.0), uv)
        +circle(0.02, vec2(cos(iTime)*0.2, sin(iTime)*0.2), uv)
        +circle(0.025, vec2(cos(iTime)*-0.3, sin(iTime)*-0.3), uv)
        +circle(0.04, vec2(sin(iTime)*-0.5, cos(iTime)*0.5), uv)
        +circle(0.05, vec2(sin(iTime)*0.8, cos(iTime)*-0.8), uv);

    fragColor = circles;
}
