void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 colorVec = vec3(0.678, 0.847, 0.901);

    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = fragCoord/iResolution.xy;
    vec2 uv3 = fragCoord/iResolution.xy;
    vec2 uv4 = fragCoord/iResolution.xy;


    uv += mod(iTime * 0.05, 512.0);
    uv2 +=  mod(iTime * 0.08, 512.0);

    uv3.x += mod(iTime * 0.01, 512.0);
    uv3.y -= mod(iTime * 0.01, 512.0);

    uv4.x -= mod(iTime * 0.01, 512.0);
    uv4.y += mod(iTime * 0.01, 512.0);

    float c1 = texture(iChannel0, uv).x;
    float c2 = texture(iChannel1, uv2 + vec2(0.2, 0.0)).x;
    float c3 = texture(iChannel0, uv3 + vec2(0.1, 0.7)).x;
    float c4 = texture(iChannel0, uv4 + vec2(0.4, 0.9)).x;


    vec4 col1 = vec4(c1, c1, c1, 1.0);
    vec4 col2 = vec4(c2, c2, c2, 1.0);
    vec4 col3 = vec4(c3, c3, c3, 1.0);
    vec4 col4 = vec4(c4, c4, c4, 1.0);


    float col = smoothstep(c2 + (c3 - c4), c1 - 1.5 * c2 + (c3 - c4), 0.3);
    col = smoothstep(col, 1.0, 0.81);

    col = 1.0 - col;
    fragColor = vec4(abs(1. - col) + 0.1, abs(1. - col)+ 0.1, abs(1. - col)+ 0.1, 1.0);


    if (col > 0.7)
    { 
       fragColor = vec4(col * colorVec.x, col * colorVec.y, col * colorVec.z, 1.0);
    }

}
