#define dt 0.001

/*
float f(float x)
{
    return tanh(x);
}
*/

// Suggested demonstration from elenzil (https://www.shadertoy.com/user/elenzil)
float f(float x)
{
    float[8] fs;
    int n = 0;
    fs[n++] =  tanh(x);
    fs[n++] =  sinh(x);
    fs[n++] =  sin (x);
    fs[n++] =  pow(x, 3.0);
    fs[n++] =  pow(x, 2.0) * 3.0;
    fs[n++] =  x * 6.0;
    fs[n++] =  x;
    fs[n++] = -x;

    float t = iTime * 0.25;

    float T = mod(t, float(fs.length() + 1));
    int n1 = int(t) % fs.length();
    int n2 = int((n1 + 1) % fs.length());

    float m = smoothstep(0.0, 1.0, fract(T) * 2.0);    

    return mix(fs[n1], fs[n2], m);
}

float df(float x)
{
    //return ( f(x + dt) - f(x) ) / dt;
    return ( f(x + dt) - f(x - dt) ) / (2.0 * dt);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 8.0 * (fragCoord - 0.5 * iResolution.xy) / max(iResolution.x, iResolution.y);

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    fragColor.x =  f(uv.x) > uv.y ? 0.5               : fragColor.x;
    fragColor.x = df(uv.x) > uv.y ? 0.5 + fragColor.x : fragColor.x;

    fragColor = fragColor.xxxw;

    if(abs(uv.x) < 0.01)
    {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    }
    if(abs(uv.y) < 0.01)
    {
        fragColor = vec4(0.0, 1.0, 0.0, 1.0);
    }
    if(uv.x * uv.x + uv.y * uv.y < 0.001)
    {
        fragColor = vec4(1.0, 1.0, 0.0, 1.0);
    }
}
