renderpass.code:
mat3 blur = mat3(
    0.111, 0.111, 0.111,
    0.111, 0.111, 0.111,
    0.111, 0.111, 0.111
);

mat3 sharpen = mat3(
    0., -1., 0.,
    -1., 5., -1.,
    0., -1., 0.
);

mat3 sunbright = mat3(
    1., 1., 1.,
    1., -5., 1.,
    1., 1., 1.
);

mat3 engrain = mat3(
    3., -2, 3.,
    -2., -3., -2.,
    3, -2, 3.
);

mat3 emboss = mat3(
    -2., -1., 0.,
    -1., 1., 1.,
    0., 1., 2.
);

//0 1 fragcoord gets one above
vec3 aSample(const int x, const int y, vec2 fragCoord)
{
    vec2 uv = fragCoord.xy/iResolution.xy * iChannelResolution[0].xy;
    uv = (uv + vec2(x, y)) / iChannelResolution[0].xy;
    return texture(iChannel0, uv).rgb;
}

vec3 aFilter(in vec2 fragCoord, mat3 kern)
{
    vec3 sum;
    for(int i = -1; i < 2; i++)
    {
        for(int j = -1; j < 2; j++)
        {
            sum += aSample(i, j, fragCoord) * kern[i+1][j+1];
        }
    }

    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    mat3 kernel = sunbright;

    // Time varying pixel color
    vec3 col = aFilter(fragCoord, kernel);

    // Output to screen
    fragColor = vec4(col,1.0);
}
