float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord -.5*iResolution.xy )/iResolution.y;
    float v = 0.0;
    vec2 pos = uv * 2.;
    float angle = 6. + sin(iTime*0.015);
    float separation = 0.0195 + sin(iTime*0.1) * 0.04;
    int maxIt = 100;
    for (int i = 0; i < maxIt; i++) {
        float s = (iTime*0.2) + float(i) * separation;
        vec2 mpos = 0.9 * vec2(sin(s * angle), - cos(s * (angle+1.) + iTime * 0.5));
        float t = 1. / length(mpos - pos);
        v += t*t * float(i + 1) / 800000.0;
    }

    fragColor = 1.0 * vec4(vec3(v, v-0.2, v+0.1), 1.0);
}
