float bell(float uvx) {
    return cos(uvx * 3.1419);
}

float wave(vec2 uv, float amp, float freq, float speed) {
    float mag = distance(uv.y, .25 + amp * sin(uv.x * freq + iTime * speed) * bell(uv.x + 0.5));
    mag = pow(mag, 100.0);
    return mag;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * .5) / iResolution.xy;
    vec4 background = vec4(vec3(0.), 0.);

    float mag1 = wave(uv, 0.125 * cos(iTime * 0.2 - 0.5), 10., 1.);
    float mag2 = wave(uv, 0.1375 * cos(iTime * 0.1), 15., 1.5);
    float mag3 = wave(uv, 0.14 * sin(iTime * 0.00005 + 0.5), 5., .5);

    float combo = mag1 + mag2 + mag3;

    vec3 waveColor = vec3(1.) - vec3(combo * .2, combo * .2, combo * .1);

	background += vec4(waveColor, combo);

    fragColor = background;
}
