vec4 twist(vec2 uv)
{
    // Offset UVs
    uv -= vec2(0.5, 0.5);

    // Twist UVs
    float angle = iTime * length(uv);
    float cAngle = cos(angle);
    float sAngle = sin(angle);
    mat2 rotMat = mat2(cAngle, -sAngle, sAngle, cAngle);
    uv = rotMat * uv;

    // Make pattern
    uv += vec2(0.5, 0.5);
    uv = mod(2.0 * uv, 1.0);
    uv -= vec2(0.5, 0.5);

    return vec4(
        uv.x > 0.0 ? 1.0 : 0.0,
        uv.y > 0.0 ? 1.0 : 0.0,
        uv.x * uv.y > 0.0 ? 0.5 : 0.0,
        1.0);
}

#define AA_DIST 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c0 = twist((fragCoord + vec2(0.5 * AA_DIST, AA_DIST)) / iResolution.xy);
    vec4 c1 = twist((fragCoord + vec2(-AA_DIST, 0.5 * AA_DIST)) / iResolution.xy);
    vec4 c2 = twist((fragCoord - vec2(0.5 * AA_DIST, AA_DIST)) / iResolution.xy);
    vec4 c3 = twist((fragCoord - vec2(-AA_DIST, 0.5 * AA_DIST)) / iResolution.xy);

    fragColor = mix(mix(c0, c1, 0.5), mix(c2, c3, 0.5), 0.5);
}
