/* Shorter version by Fabrice (188 chars) */
void mainImage(out vec4 o, vec2 u) {
    o -= o;
    for (float i=0.; i < .55; i += .05) {
        vec2 r = iResolution.xy,
             p = abs(u - .5 * r) ,
             q = vec2(-i, .5 - i); 
        p.x -= i*r.y;
        o += max(0.,1.- .5* length(p - q * max(0., dot(p, q) / dot(q, q))));
    }
}
