void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
    o -= o;
    for (float i=0.; i < .55; i += .05) {
        vec2 p = abs(u - .5 * r) / r.y - vec2(i, 0), q = vec2(-i, .5 - i);
        o += smoothstep(2. / r.y, 0., length(p - q * max(0., dot(p, q) / dot(q, q))));
    }
}
