// extracting and refactoring the paper layer in "Moving cutouts 2" by AntoineC. https://shadertoy.com/view/wd3yzf

#define T(U, x,y)   texture(iChannel0, (U) * vec2(x,y) ).r

#define Paper(U)  ( .7* ( T(U,2,.02) * T(U,.02,2.) ) + T(U,1,1) )  // --- paper texture


vec3 Bump(vec2 U, float s )  // scaling s                          // --- bump of texture
{
   U *= s;
   float h = Paper(U);

#if 0                                       // hardware derivative ok for scaling s <= .5

    return normalize( vec3( dFdx(h), dFdy(h), 7.5 ) ); 

#else                                       // use manual derivative for scaling s >= .5

   vec2 e = vec2(dFdx(U.x),0);
   return normalize ( vec3( Paper(U-e) - h , Paper(U-e.yx) - h , 7.5 ) );

#endif 
}


void mainImage( out vec4 O, vec2 u )
{

	vec2  R = iResolution.xy,
          U = ( u - .5*R ) / R.y; 
    float t = 2.*iTime, 
          s = exp2( floor(-U.x*4.)/4. ),  // scaling 
          a = .2+(.5+U.y);

    O = 1.05 * vec4( dot( Bump(U, s), vec3(cos(t)*cos(a), sin(t)*cos(a), sin(a) ) ) );
    if ( fract(U.x*4.) < 4./R.y ) O = vec4(1,0,0,0);
}
