float circle(vec2 uv, float raduis, float blur, vec2 position)
{
    float d = length(uv - position);
    return smoothstep(raduis, raduis - blur, d);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -=0.5; // map uv to -0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y; // handle aspect ratio between x and y coordinates

    float face = circle(uv, 0.3, 0.02, vec2(0.)); // face
    face -= circle(uv, 0.07, 0.02, vec2(-0.11, 0.1)); // right eye
    face -= circle(uv, 0.07, 0.02, vec2(0.11, 0.1)); // left eye

    float mouse = circle(uv, 0.2, 0.02, vec2(0.));
    mouse -= circle(uv, 0.2, 0.02, vec2(0., 0.08));

    face -= mouse;

    vec3 col = vec3(1., 1., 0) * face;

    // Output to screen
    fragColor = vec4(col,1.0);
}
