int channel;
float fractSin11(float x){ // 1 in, 1 out
    return fract(1000.0 * sin(x));
}
float fractSin21(vec2 xy){ // 2 in, 1 out
    return fract(sin(dot(xy, vec2(12.9898, 78.233))) * 43758.5453123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 pos = fragCoord.xy;
    pos += floor(60.0 * iTime);
    channel = int(2.0 * fragCoord.x / iResolution.x);
    if (channel == 0){ //left
        fragColor = vec4(fractSin11(pos.x));
    } else { //right
        fragColor = vec4(fractSin21(pos.xy / iResolution.xy));
    }
    fragColor.a = 1.0; 
}
