void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uint arr[1000];

    for(uint i = uint(0); i < uint(1000); i++){
    arr[i] = i*2u;
    }

    int index = int(fragCoord.y)*int(iResolution.x) + int(fragCoord.x);

    uint pixel = arr[index];




    float r = float((pixel >> 0) & 0x000000FFu) / 255.0;
    float g = float((pixel >> 8) & 0x000000FFu) / 255.0;
    float b = float((pixel >> 16) & 0x000000FFu) / 255.0;

    vec3 col = vec3(r,g,b);

     //col = vec3(float(pixel) / 16777216.0); // 4000000000.0

    fragColor = vec4(col,1.0);
}
